import React, { Component } from 'react';
import Header from './components/header';
// import SignIn from './components/Route/home/signin';
import Home from './components/Route/home/home';
import Swap from './components/Route/swap';
import Search from './components/Route/search';
import Blog from './components/Route/blog';
import Profile from './components/Route/profile';
import Contact from './components/Route/contact';
import { BrowserRouter as Router, Route } from "react-router-dom";
// import logo from './logo.svg';
import './App.css';

class App extends Component {
  state = {
    email: '',
    password: '',
}

handleInputChange = event => {
    let value = event.target.value;
    const name = event.target.email;

    if(name === "password") {
        value = value.substring(0, 15);
    }

    this.setState({
        [name]: value
    });
};

handleFormSubmit = event => {
    event.preventDefault();
    if(!this.state.email || !this.state.password){
        alert("Your email and password do not match")
    }
}

  render() {
    return (
      <div className="App">
        <Router>
          <div>
        <Header />

                {/* <p>
          Hello {this.state.firstName} {this.state.lastName}
        </p> */}
        <form className="form">
          <input
            value={this.state.email}
            name="Email"
            onChange={this.handleInputChange}
            type="text"
            placeholder="Email"
          />
          <input
            value={this.state.password}
            name="password"
            onChange={this.handleInputChange}
            type="password"
            placeholder="Password"
          />
          <button onClick={this.handleFormSubmit}>Submit</button>
        </form>

        <Route exact path="/" component={Home} />
        <Route exact path="/swap" component={Swap} />
        <Route exact path="/search" component={Search} />
        <Route path="/blog" component={Blog} />
        <Route path="/profile" component={Profile} />
        <Route path="/contact" component={Contact} />
        </div>
        </Router>


        {/* <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
        </header> */}
      </div>
    );
  }
}

export default App;
