      };

    render() {
        return (
            <div>
                <div> Search Page </div>
            <form>
                <label> Search a plant!</label>
                <input
            value={this.state.search_input}
            name="search_Input"
            onChange={this.handleInputChange}
            type="text"
            placeholder="search here!"
          />
           <button onClick={this.handleFormSubmit}>Submit</button>
            </form>
            </div>
        )
    }
}


export default Search;
