import React, { Component } from 'react';
import API from "../../utils/API"
// import jwt_decode from 'jwt-decode'
import Header from '../../components/header/header'



class Profile extends Component {
    state = {
        user: "",
        first_name: "",
        zipCode: "",
        about: "",
        id: "",
        email: "",
      };

    componentDidMount() {        
      console.log(localStorage)
      const currentUser = localStorage.user
      console.log(currentUser)

      // const user  = this.state.user
      const data = currentUser
      console.log(data)
      API.findUser(data).then(res => {
            console.log(res)
            console.log(res.data.first_name)
            console.log(res.data.zipCode)
            console.log(res.data.id)
            this.setState({first_name: res.data.first_name, zipCode: res.data.zipCode, about: res.data.about, id: res.data.id, email:res.data.email} )
            console.log(this.state.id)
            localStorage.setItem('id', this.state.id)
            localStorage.setItem('user', this.state.email)
            console.log(localStorage)
          })
          .catch(err => console.log(err));



      };
      // this.setState({ 
      //   user: localStorage.user
      // })
      // console.log(this.state.user)
      // this.loadCurrentUsers();



      // loadCurrentUsers = () => {
      //   const data = this.state.user
      //   API.getUser(data)
      //     .then(res => 
      //       console.log(res)
      //       // this.setState({ users: res.data, first_name: "", zipCode: "", about: "" }),
      //       // console.log(this.state.users)
      //     )
      //     .catch(err => console.log(err));

      // };


    render() {
        return (
        <div> 
          <Header/>
        <div className="profile.body">
        <div className= "container">
        <div className="row">
            <div className="col-md-3 ">
                    <h1 className="text-center">Profile</h1>
            </div>
            </div>
            <div className="row">
            <h3> Hey there, {this.state.first_name}! </h3>
            </div>
            <div className="row">
            <h3> Current Location : {this.state.zipCode}</h3>
            </div>
            <div className="row">
            <h3> Bio : {this.state.ze}</h3>
            </div>
            <table className="table col-md-6 mx-auto">
                <tbody>
                    <tr>
                        <td></td>
                        <td>{this.state.first_name}</td>
                    </tr>
                    <tr>
                        <td>Last Name</td>
                        <td></td>
                    </tr>
                    <tr>
                        <td></td>Email
                        <td>{this.state.about}</td>
                    </tr>
                </tbody>
            </table>

        </div>
    </div>
     </div>
        )
    }
}


export default Profile;
